import { useState } from 'react' ;
import Graphic from './Graphic' ;
import { Size } from '../../../common/types';


type Props = {
    link: string,
    link_text: string,
    graphic_desc: string,
    graphic_src: string,
}
const SocialIcon = ({ link, link_text, graphic_desc, graphic_src } : Props) => {

    const [isHover, setHover] = useState(false) ;

    const handleHover = () => {
        setHover(true) ;
    }

    const handleNotHover = () => {
        setHover(false) ;
    }

    return (
        <a className='enclosed' href={link} onMouseOver={handleHover} onMouseLeave={handleNotHover}>
            <Graphic size={Size.Medium} graphic_src={graphic_src} desc={graphic_desc}/>
            { isHover ?
                <p className='text-bubble'> {link_text}</p>
                : null}
        </a>
    )

}

export default SocialIcon ;
