// Site Navigation and Routing
import { BrowserRouter as Router, Routes, Route, Outlet } from 'react-router-dom'
import { useState, useEffect } from 'react';

// Contexts
import { ThemeContextProvider } from './contexts/ThemeContext' ;
import { useThemeContext } from './contexts/ThemeContext'

// All Styles
import '../styles/global_styles.css'
import '../styles/component_styles.css'
import '../styles/section_styles.css'
import '../styles/animations.css'

// Pages
import Main from '../pages/Main' ;
import Projects from '../pages/Projects' ;

// Individual Components
import Header from '../pages/components/large/Header' ;
import Footer from '../pages/components/large/Footer' ;

const PageLayout = () => {
    const { getTheme } = useThemeContext() ;

    let theme = getTheme() ;
    // add (dis)appearing effect.
    const [toggle, setToggle] = useState<boolean | null>(null) ;
    useEffect(() => {

        // save last page pos; 0 on page load.
        let lastPos = window.pageYOffset ;

        // only toggle header when 15 or more pixels have been scrolled.
        let threshold = 40 ;
        console.log("Init")

        const getScrollDir = () => {
            let currPos = window.pageYOffset ;

            console.log(currPos)
            console.log("ran")

            // user scrolling down ? Don't show header.
            let showHeader = currPos > lastPos ? false : true ;

            // user scrolling in a direction already detected? Don't update.
            if ( showHeader !== toggle && ( 
                (currPos - lastPos > threshold) || (currPos - lastPos > -threshold)))
            {
                setToggle(showHeader) ;
                lastPos = currPos ;
            }

            //
        } ;

        document.addEventListener("scroll", getScrollDir) ;

        // remove EL as part of cleanup
        return (() => document.removeEventListener("scroll", getScrollDir)) ;
    }, [ toggle ] ) ; // prevent element reloads when toggle changed.

    return (
        <div className={`page-container background ${theme} `}>
            <Header />
            <Outlet />
            <Footer />
        </div>
    )
}

const SiteLayout = () => {
    return (
        <Router>
            <ThemeContextProvider> 
                <div className='site-container'>
                    <Routes>
                        <Route path='/' element={<PageLayout/>}>
                            <Route index element={<Main />} />
                            <Route path='projects' element={<Projects/>} />
                        </Route>
                    </Routes>
                </div>
            </ThemeContextProvider>
        </Router>
    )
}

export default SiteLayout
